import React from 'react';
import styles from './Track.module.css';



function Track(props){



    function handleAddClick(event) {
        props.updatePlaylistTracks(event.target.trackId)
        console.log(typeof event.target.trackId)
    }


    return (
        <div className={styles.TrackContainer} trackId={props.trackData.id}>
            <span className={styles.Track}>
                {props.trackData.name} by {props.trackData.artists[0].name} id is {props.trackData.id} 
            </span>
            <button className={styles.RemoveButton} onClick={() => handleAddClick(props.trackData.id)}>+</button>

        </div>
    )
}

export default Track;
