const apiKey = "BQAER5nP1ARbGNtKL-Ty4upC51pDdTaTmBUp11dFo1IebT_0xSqyP5fimBmQcwvnQlYVarHDFvfbWuax7rqOdoLcMUsdxBIe62Z09YLsttn2dSpR_ZZdTY3lYwTuxMSoV6rtoyqHoRx4jGHTBJuq22ur9MJsLrel5APqbdUYDQMJ75U9DXrtj5fy81D6Yd1Ba7Nhbw" // Access token à renouveler toutes les heures ?

const Spotify = {
    search(term) {
        return fetch(
            `https://api.spotify.com/v1/search?type=TRACK&q=${term}`,
            {
                headers: {
                    Authorization: `Bearer ${apiKey}` 
                },
            }

        )
        .then((response) => {
            return response.json();
        })
        .then((jsonResponse) => {
            if (jsonResponse.tracks) { // track est il bien le bon suffixe ?
                return jsonResponse.tracks.map((track) => ({
                    id: track.id,
                    name: track.name,
                    artist: track.artist,
                    album: track.album,
                    url: track.url,
                }))
            }
        })
    }
}

export default Spotify;
