const apiKey = "BQABjEOT7MyEkStUXc7Astxia94mA4HnqTTQruljyO5qG1zBBRB41Ck1YJUD8ab-wPIIM_LQcYqow_DiUX5SKcXLT8w510V2GOovlxyiKo-6t0_yWjqiSes5q0TCX6IRWj00CGfjOv9nq27JyO2L5o65cLKLgioeyYLuJyVSwCK0WZ8EFrwf5PY_5ijTEAHt7f5EKA" // Access token à renouveler toutes les heures ?

const Spotify = {
    search(term) {
        return fetch(
            `https://api.spotify.com/v1/search?q=bonjour&type=track`,
            {
                headers: {
                    Authorization: `Bearer ${apiKey}` 
                },
            }

        )
        .then((response) => {
            return response.json();
        })
        .then((jsonResponse) => {

            // console.log(jsonResponse);
            // console.log('jsonResponse is an ' + typeof jsonResponse);
            // console.log(jsonResponse.tracks)
            // console.log(jsonResponse.tracks.items)
            const items = jsonResponse.tracks.items;
            // const arrayFromJson = Array.from(jsonResponse.tracks.items);

            // console.log('arrayFromJson is an ' + typeof arrayFromJson);
            // console.log('arrayFromJson is ' + typeof arrayFromJson);
            // console.log(arrayFromJson);

            console.log(items[0]);

            const arrayFromJsonMap = items.map((track, index) => {
                [index] = {
                    id: track.id,
                    name: track.name,
                    artist: track.artists[0].name,
                    album: track.album,
                    url: track.uri,
                }
            })

            console.log(arrayFromJsonMap);
            return arrayFromJsonMap;



            /*
            if (arrayFromJson) { // track est il bien le bon suffixe ?
                return arrayFromJson.map((track) => ({
                    id: track.id,
                    name: track.name,
                    artist: track.artist,
                    album: track.album,
                    url: track.url,
                }))
            } */
        })
    }
}



export default Spotify;
