const apiKey = "BQABjEOT7MyEkStUXc7Astxia94mA4HnqTTQruljyO5qG1zBBRB41Ck1YJUD8ab-wPIIM_LQcYqow_DiUX5SKcXLT8w510V2GOovlxyiKo-6t0_yWjqiSes5q0TCX6IRWj00CGfjOv9nq27JyO2L5o65cLKLgioeyYLuJyVSwCK0WZ8EFrwf5PY_5ijTEAHt7f5EKA" // Access token à renouveler toutes les heures ?

const Spotify = {
    search(term) {
        return fetch(
            `https://api.spotify.com/v1/search?type=TRACK&q=${term}`,
            {
                headers: {
                    Authorization: `Bearer ${apiKey}` 
                },
            }

        )
        .then((response) => {
            return response.json();
        })
        .then((jsonResponse) => {
            if (jsonResponse.tracks) { // track est il bien le bon suffixe ?
                return jsonResponse.tracks.map((track) => ({
                    id: track.id,
                    name: track.name,
                    artist: track.artist,
                    album: track.album,
                    url: track.url,
                }))
            }
        })
    }
}

export default Spotify;
